import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './FuelData.css';

const FuelData = ({ aircraft, distance }) => {
    const [fuelData, setFuelData] = useState(null);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchFuelData = async () => {
            try {
                // Fetch from your backend proxy instead of directly from despouy.ca
                const response = await axios.get(`/api/fuel-data?aircraft=${aircraft}&distance=${distance}`);
                setFuelData(response.data);
                setLoading(false);
            } catch (error) {
                // ... error handling
            }
        };

        fetchFuelData();
    }, [aircraft, distance]);

    if (loading) {
        return (
            <div className="loading-spinner">
                <div className="spinner"></div>
                <p>Loading fuel data...</p>
            </div>
        );
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div className="fuel-data">
            <h3>Fuel Data</h3>
            <p>Aircraft: {aircraft}</p>
            <p>Distance: {distance} nm</p>
            <p>Fuel Required: {fuelData.fuel_required} kg</p>
        </div>
    );
};

export default FuelData;
