import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './FlightSelection.css';

const FlightSearch = () => {
  const [fromICAO, setFromICAO] = useState('');
  const [toICAO, setToICAO] = useState('');
  const [flights, setFlights] = useState([]);
  const navigate = useNavigate();

  const fetchFlights = async () => {
    try {
      const response = await axios.get(`https://api.flightplandatabase.com/search/plans?fromICAO=${fromICAO}&toICAO=${toICAO}`);
      setFlights(response.data);
    } catch (error) {
      console.error('Error fetching flight plans:', error);
    }
  };

  const handleFlightClick = (flightId) => {
    navigate(`/flight/${flightId}`);
  };

  return (
    <div className="flight-search-container">
      <h1>Flight Plan Search</h1>
      <div className="search-form">
        <input 
          type="text" 
          placeholder="Enter From ICAO" 
          value={fromICAO} 
          onChange={(e) => setFromICAO(e.target.value)} 
        />
        <input 
          type="text" 
          placeholder="Enter To ICAO" 
          value={toICAO} 
          onChange={(e) => setToICAO(e.target.value)} 
        />
        <button onClick={fetchFlights}>Search</button>
      </div>
      <div className="flights-list">
        {flights.map(flight => (
          <div 
            key={flight.id} 
            className="flight-box"
            onClick={() => handleFlightClick(flight.id)}
          >
            <p><strong>ID:</strong> {flight.id}</p>
            <p><strong>From Name:</strong> {flight.fromName}</p>
            <p><strong>To Name:</strong> {flight.toName}</p>
            <p><strong>Distance:</strong> {flight.distance.toFixed(2)} km</p>
            <p><strong>From ICAO:</strong> {flight.fromICAO}</p>
            <p><strong>To ICAO:</strong> {flight.toICAO}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FlightSearch;
