import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import FlightSearch from './Components/FlightSelection';
import FlightDetail from './Components/FlightDetail';
import './App.css';

const App = () => {
  const [fromICAO, setFromICAO] = useState('');
  const [toICAO, setToICAO] = useState('');
  const [flights, setFlights] = useState([]);
  const location = useLocation(); // Get the current location

  useEffect(() => {
    // This useEffect will run every time the route location changes
    // This ensures that the FlightSearch component refreshes when navigating back to it
    console.log("Route location changed: ", location);
    // Add any additional logic here if needed
  }, [location]); // Trigger the useEffect when location changes

  return (
    <Router>
      <div className="app-container">
        <Routes>
          <Route
            path="/"
            element={
              <FlightSearch
                fromICAO={fromICAO}
                toICAO={toICAO}
                flights={flights}
                setFromICAO={setFromICAO}
                setToICAO={setToICAO}
                setFlights={setFlights}
              />
            }
          />
          <Route path="/flight/:id" element={<FlightDetail />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
